# main loop
def Print_Color(color):
    global cerna 
    for e in color:
        if e == -1:
            push_up()
            NRadek()
            cerna = False
            continue
        if e == sirka:
            push_up()
            NRadek()
            cerna = False
            continue
        if cerna == True:
            push_down()
        else:
            #cerna == False:
            push_up()

        carka(e)
        cerna = not cerna

        
